'use strict';var themeBtn=document.querySelector('.theme-button'),upBtn=document.querySelector('.up-button'),body=document.body;window.addEventListener('load',function(){themeBtn.addEventListener('click',changeTheme),scrollFade(),document.addEventListener('scroll',scrollFade),upBtn.addEventListener('click',function(){document.scrollingElement.scrollTop=0})});function changeTheme(){body.style.backgroundColor='hsl('+random(0,360)+','+random(0,100)+'%,'+random(0,55)+'%)'}function scrollFade(){upBtn.style.display=300<document.scrollingElement.scrollTop?'block':'none'}function random(a,b){return Math.floor(Math.random()*(b-a+1)+a)}